import React from 'react';
import { useDispatch, useSelector } from "react-redux";

const words = [
    'apple',
    'pear',
    'banana',
    'passionfruit',
    'pumpkin',
    'brocolli',
    'mango',
    'orange'
]

function Word() {
    const dispatch = useDispatch();
    const randomWord = useSelector(storeState => storeState.randomWord);
    const [inputLetters, setInputLetters] = React.useState(''); //displays the randomWord as _

    React.useEffect(() => {
        if (randomWord === '') {
            //generate random character here and store in redux store
            let min = 0;
            let max = 7;
            let randomNumber = Math.round(Math.random() * (max - min) + min);
            let chosenRandomWord = words[randomNumber];
            console.log('random word: ', chosenRandomWord);
            dispatch({ type: "SET_RANDOM_WORD", payload: chosenRandomWord });
            let wordArrayed = [];
            //console.log(chosenRandomWord.split(""), 'randomWordArrayed')
            chosenRandomWord.split("").forEach(letter => {
                wordArrayed.push('_ ');
            }); 
            console.log(wordArrayed, 'wordArrayed');
            setInputLetters(wordArrayed);
        } 
    }, [randomWord])

    return (
        <div className="App">
            <p>{inputLetters}</p>
            <br />
            <label>Enter letter: </label>
            <input type="text" placeholder="e.g a"/>
            <button>submit</button>
        </div>
    );
}

export default Word;
