import React from 'react';
import { useSelector } from "react-redux";

function Hangman() {
    const incorrectCounter = useSelector(storeState => storeState.incorrectCounter);
    //const canvas = document.querySelector('#canvas'); //FIX THIS
    //const ctx = canvas.getContext('2d');
    let ctx;
    //ctx.lineWidth = 3;
    //ctx.strokeStyle = '#003300';

    window.addEventListener("load", () => {
        const canvas = document.querySelector('#canvas'); //FIX THIS
        ctx = canvas.getContext('2d');
        ctx.lineWidth = 3;
        ctx.strokeStyle = '#003300';
    })

    React.useEffect(() => {
        switch(incorrectCounter) {
            case 1:
                firstMark(ctx);
            case 2:
                secondMark(ctx);
            case 3:
                thirdMark(ctx);
            case 4:
                fourthMark(ctx); 
            case 5:
                fifthMark(ctx);   
            case 6:
                sixthMark(ctx);  
            case 7:
                seventhMark(ctx); 
            case 8:
                eighthMark(ctx); 
            case 9:
                ninthMark(ctx); 
            default:
                console.log('nothing');
        }
    }, [incorrectCounter])

    window.addEventListener("load", () => {

        // firstMark(ctx);
        // secondMark(ctx);
        // thirdMark(ctx);
        // fourthMark(ctx);
        // fifthMark(ctx);
        // sixthMark(ctx);
        // seventhMark(ctx);
        // eighthMark(ctx);
        // ninthMark(ctx);
    })

    return (
    <div className="App">
        <canvas id="canvas" ></canvas>
    </div>
    );
}

function firstMark(ctx) {
    ctx.moveTo(10,130);
    ctx.lineTo(40,130);
    ctx.stroke();
}

function secondMark(ctx) {
    ctx.moveTo(25,40);
    ctx.lineTo(25,130);
    ctx.stroke();
}

function thirdMark(ctx) {
     ctx.moveTo(25,40);
     ctx.lineTo(60,40);
     ctx.stroke();
}

function fourthMark(ctx) {
    ctx.moveTo(60,40);
    ctx.lineTo(60,50);
    ctx.stroke();
}

function fifthMark(ctx) {
    let centerX = 60;
    let centerY = 60;
    let radius = 10;
    ctx.beginPath();
    ctx.arc(centerX, centerY, radius, 0, 2 * Math.PI, false);
    ctx.stroke();
}

function sixthMark(ctx) {
    ctx.moveTo(60,70);
    ctx.lineTo(60,100);
    ctx.stroke();
}

function seventhMark(ctx) {
    ctx.moveTo(45,85);
    ctx.lineTo(75,85);
    ctx.stroke();
}

function eighthMark(ctx) {
    ctx.moveTo(60,100);
    ctx.lineTo(50,120);
    ctx.stroke();
}

function ninthMark(ctx) {
    ctx.moveTo(60,100);
    ctx.lineTo(70,120);
    ctx.stroke();
}



export default Hangman;
