export const initialState = {
  randomWord: ''
};

export const reducer = (state = initialState, action) => {
    switch (action.type) {
        case "SET_PROMPT":
            return {
                generatedPrompt: state.generatedPrompt 
            };

        default:
            return state;
    }
  };



  export const addToInputs = word => {
    return {
      type: "ADD_TO_INPUTS",
      payload: word
    };
  };

  export const incrementScore = score => {
    return {
      type: "INCREMENT_SCORE",
      payload: score
    };
  };

  export const addToTotalScores = newScore => {
    return {
      type: "ADD_ON_SCORE",
      payload: newScore
    };
  };
