import React from 'react';
import logo from './logo.svg';
import './App.css';
import { useSelector } from "react-redux";

import Word from './components/Word';
import Hangman from './components/Hangman';
import Letters from './components/Letters';
import TopicButton from './components/TopicButton'

import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const gameWon = useSelector(storeState => storeState.gameWon);
  const [chosenTopic, setChosenTopic] = React.useState('');

  return (
    <div class="container">
      <b><h1>Hangman</h1></b>
      <div class="text-center">
        {
          (chosenTopic !== '') 
          ?  
            <>
              <Word />
              <br />
              <div>
                {

                  (gameWon)
                  ? <h2 class="text-center">ğŸ”¥ğŸ†You've won!ğŸ†ğŸ”¥</h2>
                  : 
                    <div>
                      <Hangman />
                      <Letters />
                    </div>
                }
              </div> 
            </>
          : 
            <>
              <h3>choose a topic!</h3>
              <TopicButton topicName={'food'} setChosenTopic={setChosenTopic} variantName={'info'}/>
              <TopicButton topicName={'animals'} setChosenTopic={setChosenTopic}/>
              <TopicButton topicName={'travel'} setChosenTopic={setChosenTopic}/>
            </>

        }
      </div>



    </div>
  );
}

export default App;
