export const initialState = {

    inputs: [],
    score: 0,
    totalScores: JSON.parse(localStorage.getItem('totalScores')) || []
};

export const reducer = (state = initialState, action) => {
    switch (action.type) {
        case "SET_PROMPT":
            return {
                generatedPrompt: state.generatedPrompt 
            };
        case "ADD_TO_INPUTS":
            return {
                ...state,
                inputs: [...state.inputs, action.payload]
            };
        case "INCREMENT_SCORE":
            return {
                ...state,
                score: state.score + action.payload 
            }
        case "ADD_ON_SCORE":
            console.log('adding', action.payload, 'to totalScores');
            const totalScores = [...state.totalScores, action.payload || []]
            localStorage.setItem("totalScores", JSON.stringify(totalScores))
            return {
                ...state,
                totalScores
            }
        default:
            return state;
    }
  };



  export const addToInputs = word => {
    return {
      type: "ADD_TO_INPUTS",
      payload: word
    };
  };

  export const incrementScore = score => {
    return {
      type: "INCREMENT_SCORE",
      payload: score
    };
  };

  export const addToTotalScores = newScore => {
    return {
      type: "ADD_ON_SCORE",
      payload: newScore
    };
  };
