import React from 'react';
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";
import Button from 'react-bootstrap/Button';

const words = [
    'apple',
    'pear',
    'banana',
    'passionfruit',
    'pumpkin',
    'brocolli',
    'mango',
    'orange'
]

function Word({ topic }) {
    const dispatch = useDispatch();
    const randomWord = useSelector(storeState => storeState.randomWord);
    const [inputLetters, setInputLetters] = React.useState(''); //displays the randomWord as _
    const [enteredLetter, setEnteredLetter] = React.useState('');
    const gameOver = useSelector(storeState => storeState.gameOver); //use ternary to display randomWord when true
    const incorrectLetters = useSelector(storeState => storeState.incorrectLetters);
    const [errorMsg, setErrorMsg] = React.useState('');

    React.useEffect(() => {
        if (randomWord === '') {
            let min = 0;
            let max = 7;
            let randomNumber = Math.round(Math.random() * (max - min) + min);
            let chosenRandomWord = words[randomNumber];
            console.log('random word: ', chosenRandomWord);
            dispatch({ type: "SET_RANDOM_WORD", payload: chosenRandomWord });
            let wordArrayed = [];
            chosenRandomWord.split("").forEach(letter => {
                wordArrayed.push('_ ');
            }); 
            setInputLetters(wordArrayed);
        } 
    }, [randomWord])

    React.useEffect(() => {
        if (randomWord === '') {
            axios
                .get(
                    `https://cors-anywhere.herokuapp.com/https://api.datamuse.com/words`, {
                        params: {
                            ml: topic
                        }
                    }
                )
                .then(response => {
                    console.log(response.data)
                    //setData(response.data);

                });






            // console.log('random word: ', chosenRandomWord);
            // dispatch({ type: "SET_RANDOM_WORD", payload: chosenRandomWord });
            // let wordArrayed = [];
            // chosenRandomWord.split("").forEach(letter => {
            //     wordArrayed.push('_ ');
            // }); 
            // setInputLetters(wordArrayed);
        } 
    }, [randomWord])


    if (gameOver) {
        return (
            <div class="text-center">
                <h3>GAME OVER</h3>
                <h2>'{randomWord}'</h2>
            </div>
        )
    } else {
        return (
            <div className="App">
                <h2>{inputLetters}</h2>
                <br />
                <label>Enter letter: </label>
                <input 
                    type="text" 
                    placeholder="e.g a"
                    onChange={(event) => {
                        setEnteredLetter(event.target.value)
                    }}
                    value={enteredLetter}
                    required
                />
                <Button
                    variant="outline-success"
                    onClick={() => {
                        //check if valid then do all these things else display error message
                        if (isValidInput(enteredLetter, inputLetters, incorrectLetters, setErrorMsg)) {
                            console.log(enteredLetter, 'enteredLetter');
                            console.log(isLetterCorrect(randomWord, enteredLetter), 'isLetterCorrect')
                            if (isLetterCorrect(randomWord, enteredLetter)) {
                                setInputLetters(showCorrectLetter(randomWord, enteredLetter, inputLetters));
                            } else {
                                dispatch({ type: "ADD_INCORRECT_LETTER", payload: enteredLetter });
                                dispatch({ type: "INCREMENT_INCORRECT_COUNTER" });
                            }
                            //console.log(inputLetters.join(""), 'inputletters as string')
                            if (inputLetters.join("") === randomWord) {
                                console.log('youve won!');
                                dispatch({ type: "TOGGLE_GAME_WON", payload: true});
                            }
                        } else {
                            console.log(errorMsg, 'errorMSG')
                        }
                        setEnteredLetter('');
                    }}
                >submit</Button>
                <div>
                    {
                        (errorMsg !== '')
                        ? <h4>{errorMsg}</h4>
                        : null
                    }
                </div>
            </div>
        );
    }               

}

function isValidInput(input, alreadyInputtedCorrect, alreadyInputtedIncorrect, setErrorMsg) {
    //console.log(typeof input, 'typeof input')
    //console.log(isNaN(parseInt(input)), 'evaluating if number')
    // if (typeof input !== 'string') {
    //     //setErrorMsg('Please enter a letter');
    //     return false;
    /*} else */if (input.length > 1) {
        setErrorMsg('Please enter a single letter🧐');
        return false;
    } else if (alreadyInputtedCorrect.includes(input)) {
        setErrorMsg('You have already entered this letter😅');
        return false;
    } else if (alreadyInputtedIncorrect.includes(input)) {
        setErrorMsg('You have already entered this letter😅');
        return false;
    } else {
        return true;
    }
}

function isLetterCorrect(word, letter) {
    if (word.includes(letter)) {
        return true;
    } else {
        return false;
    }
}

function showCorrectLetter(word, letter, showingWord) {
    let indexes = getAllIndexes(word.split(""), letter);
    indexes.map(index => {
        showingWord[index] = letter
    })
    //console.log(showingWord, 'inputLetters now');
    return showingWord;
}

function getAllIndexes(arr, val) {
    var indexes = [], i;
    for(i = 0; i < arr.length; i++)
        if (arr[i] === val)
            indexes.push(i);
    return indexes;
}

export default Word;
